script.lang:
		CREATE OR REPLACE VIEW v_budget_code_project_id AS
		SELECT a.amp_activity_id, a.budget_code_project_id
   		FROM amp_activity a
  		ORDER BY a.amp_activity_id;

		INSERT INTO amp_columns (columnId,columnName,aliasName,cellType,extractorView) VALUES 
		((Select (max(columnId) + 1) from amp_columns),'Budget Code Project Id','budget code project id','org.dgfoundation.amp.ar.cell.TextCell','v_budget_code_project_id');
