script.lang:
            	 CREATE OR REPLACE FUNCTION getparentsubsubsectorid(sectorid bigint)
  				 RETURNS bigint AS
				$BODY$
				declare
					secid bigint;
					depth int;
				BEGIN      	 	
					select getSectorDepth(sectorId) into depth; 
					if (depth = 2) then return sectorId;
					end if; 	
				return 0;
				END;
				$BODY$
  				LANGUAGE plpgsql VOLATILE@
