var utils = {};
var _ = require('underscore');
utils.isIntStr = function(n) {
	// test whether a string starts with is a base-10 int
	return !isNaN(parseInt(n, 10))
};

utils.tagIf = function (test, tag) {
	return function(setting) {
		if (test(setting)) {
			setting[tag] = true;
		}
		return setting;
	}
};
utils.isNullOrUndefined = function(input){
	return _.isUndefined(input) || _.isNull(input);
}

module.exports = utils;