CREATE or replace VIEW  `v_contribution_funding` AS select `eu`.`amp_activity_id` AS `amp_activity_id`,`eu`.`id` AS `amp_funding_id`,`euc`.`id` AS `amp_funding_detail_id`,`o`.`name` AS `donor_name`,`euc`.`amount` AS `transaction_amount`,`euc`.`transaction_date` AS `transaction_date`,`c`.`currency_code` AS `currency_code`,`acv_term`.`category_value` AS `terms_assist_name`,`acv_mod`.`category_value` AS `financing_instrument_name` from (((((`amp_eu_activity` `eu` join `amp_eu_activity_contributions` `euc`) join `amp_currency` `c`) join `amp_category_value` `acv_term`) join `amp_category_value` `acv_mod`) join `amp_organisation` `o`) where ((`eu`.`id` = `euc`.`eu_activity_id`) and (`euc`.`amount_currency` = `c`.`amp_currency_id`) and (`acv_term`.`id` = `euc`.`financing_type_categ_val_id`) and (`acv_mod`.`id` = `euc`.`financing_instr_category_value_id`) and (`o`.`amp_org_id` = `euc`.`donor_id`)) order by `eu`.`amp_activity_id`;