CREATE OR REPLACE VIEW `v_regional_funding` AS select `f`.`activity_id` AS `amp_activity_id`,`f`.`amp_regional_funding_id` AS `amp_regional_funding_id`,`f`.`amp_regional_funding_id` AS `amp_fund_detail_id`,`r`.`name` AS `region_name`,`f`.`transaction_type` AS `transaction_type`,`f`.`adjustment_type` AS `adjustment_type`,date_format(`f`.`transaction_date`,_latin1'%Y-%m-%d') AS `transaction_date`,`f`.`transaction_amount` AS `transaction_amount`,`c`.`currency_code` AS `currency_code`,`p`.`code` AS `perspective_code`,`f`.`region_id` AS `region_id` from (`amp_perspective` `p` join ((`amp_regional_funding` `f` join `amp_region` `r`) join `amp_currency` `c`)) where ((`c`.`amp_currency_id` = `f`.`currency_id`) and (`f`.`region_id` = `r`.`amp_region_id`)) order by `f`.`activity_id`;
CREATE OR REPLACE VIEW `v_contribution_funding` AS select `eu`.`amp_activity_id` AS `amp_activity_id`,`eu`.`id` AS `amp_funding_id`,`euc`.`id` AS `amp_funding_detail_id`,`o`.`name` AS `donor_name`,`euc`.`amount` AS `transaction_amount`,`euc`.`transaction_date` AS `transaction_date`,`c`.`currency_code` AS `currency_code`,`acv_term`.`category_value` AS `terms_assist_name`,`acv_mod`.`category_value` AS `financing_instrument_name`,`o`.`amp_org_id` AS `amp_org_id` from (((((`amp_eu_activity` `eu` join `amp_eu_activity_contributions` `euc`) join `amp_currency` `c`) join `amp_category_value` `acv_term`) join `amp_category_value` `acv_mod`) join `amp_organisation` `o`) where ((`eu`.`id` = `euc`.`eu_activity_id`) and (`euc`.`amount_currency` = `c`.`amp_currency_id`) and (`acv_term`.`id` = `euc`.`financing_type_id`) and (`acv_mod`.`id` = `euc`.`financing_instrument_id`) and (`o`.`amp_org_id` = `euc`.`donor_id`)) order by `eu`.`amp_activity_id`;
CREATE OR REPLACE VIEW `v_component_funding` AS select `f`.`activity_id` AS `amp_activity_id`,`f`.`amp_component_funding_id` AS `amp_component_funding_id`,`f`.`amp_component_funding_id` AS `amp_fund_detail_id`,`c`.`title` AS `component_name`,`f`.`transaction_type` AS `transaction_type`,`f`.`adjustment_type` AS `adjustment_type`,date_format(`f`.`transaction_date`,_latin1'%Y-%m-%d') AS `transaction_date`,`f`.`transaction_amount` AS `transaction_amount`,`f`.`currency_id` AS `currency_id`,`cu`.`currency_code` AS `currency_code`,`p`.`code` AS `perspective_code`,`f`.`amp_component_id` AS `amp_component_id` from (`amp_perspective` `p` join ((`amp_component_funding` `f` join `amp_components` `c`) join `amp_currency` `cu`)) where ((`cu`.`amp_currency_id` = `f`.`currency_id`) and (`f`.`amp_component_id` = `c`.`amp_component_id`)) order by `f`.`activity_id`;
CREATE OR REPLACE VIEW `v_donor_funding` AS select `f`.`amp_activity_id` AS `amp_activity_id`,`f`.`amp_funding_id` AS `amp_funding_id`,`fd`.`amp_fund_detail_id` AS `amp_fund_detail_id`,`d`.`name` AS `donor_name`,`fd`.`transaction_type` AS `transaction_type`,`fd`.`adjustment_type` AS `adjustment_type`,date_format(`fd`.`transaction_date`,_latin1'%Y-%m-%d') AS `transaction_date`,`fd`.`transaction_amount` AS `transaction_amount`,`c`.`currency_code` AS `currency_code`,`cval`.`category_value` AS `terms_assist_name`,`fd`.`fixed_exchange_rate` AS `fixed_exchange_rate`,`p`.`code` AS `perspective_code`,`d`.`org_grp_id` AS `org_grp_id` from (((((`amp_funding` `f` join `amp_funding_detail` `fd`) join `amp_category_value` `cval`) join `amp_currency` `c`) join `amp_organisation` `d`) join `amp_perspective` `p`) where ((`c`.`amp_currency_id` = `fd`.`amp_currency_id`) and (`f`.`amp_funding_id` = `fd`.`AMP_FUNDING_ID`) and (`cval`.`id` = `f`.`type_of_assistance_category_value_id`) and (`d`.`amp_org_id` = `f`.`amp_donor_org_id`)) order by `f`.`amp_activity_id`;