CREATE OR REPLACE VIEW `v_costs` AS select `eu`.`amp_activity_id` AS `amp_activity_id`,`eu`.`id` AS `eu_id`,`eu`.`total_cost` AS `cost`,`c`.`currency_code` AS `currency_code`,`eu`.`transaction_date` AS `currency_date`,`getExchange`(`c`.`currency_code`,`eu`.`transaction_date`) AS `exchange_rate` from (`amp_niger_staging`.`amp_eu_activity` `eu` join `amp_niger_staging`.`amp_currency` `c`) where (`eu`.`total_cost_currency_id` = `c`.`amp_currency_id`);
