CREATE or replace VIEW `v_on_off_budget` AS select `a`.`amp_activity_id` AS `amp_activity_id`,IF(`a`.`budget`=true,"yes","no") AS `budget` from `amp_activity` `a` order by `a`.`amp_activity_id`;
insert into amp_columns(columnName, cellType, extractorview) values("On/Off Budget", "org.dgfoundation.amp.ar.cell.TextCell","v_on_off_budget");
INSERT INTO `amp_columns_order`(`columnName`,`indexOrder`) SELECT 'Budget', max(`indexOrder`) + 1 from `amp_columns_order`;