CREATE OR REPLACE VIEW `v_sectors` AS select `sa`.`amp_activity_id` AS `amp_activity_id`,`getSectorName`(`getParentSectorId`(`s`.`amp_sector_id`)) AS `sectorname`,`getParentSectorId`(`s`.`amp_sector_id`) AS `amp_sector_id`,sum(`sa`.`sector_percentage`) AS `sector_percentage`,`s`.`amp_sec_scheme_id` AS `amp_sector_scheme_id`,`ss`.`sec_scheme_name` AS `sec_scheme_name` from (((`amp_sector_scheme` `ss` join `amp_classification_config` `cc` on(((`cc`.`name` = _latin1'Primary') and (`cc`.`classification_id` = `ss`.`amp_sec_scheme_id`)))) join `amp_sector` `s` on((`s`.`amp_sec_scheme_id` = `ss`.`amp_sec_scheme_id`))) join `amp_activity_sector` `sa` on(((`sa`.`amp_sector_id` = `s`.`amp_sector_id`) and (`sa`.`classification_config_id` = `cc`.`id`)))) group by `sa`.`amp_activity_id`,`getParentSectorId`(`s`.`amp_sector_id`) order by `sa`.`amp_activity_id`,`getSectorName`(`getParentSectorId`(`s`.`amp_sector_id`));