CREATE or replace VIEW `v_sub_sectors` AS select `sa`.`amp_activity_id` AS `amp_activity_id`,`s`.`name` AS `name`,`s`.`amp_sector_id` AS `amp_sector_id`,`sa`.`sector_percentage` AS `sector_percentage` from (`amp_activity_sector` `sa` join `amp_sector` `s`  join `amp_sector_scheme` `ss` on((`sa`.`amp_sector_id` = `s`.`amp_sector_id`)) ) where (`s`.`sector_code` > 1000) and (`s`.`amp_sec_scheme_id` in (select classification_id from amp_classification_config where name='Primary')) and (`s`.`amp_sec_scheme_id` =  `ss`.`amp_sec_scheme_id`) order by `sa`.`amp_activity_id`,`s`.`name`;