ALTER TABLE amp_category_class ALTER COLUMN id SET DEFAULT nextval('amp_category_class_seq');
ALTER TABLE amp_category_value ALTER COLUMN id SET DEFAULT nextval('amp_category_value_seq');
UPDATE  amp_indicator_values   ind SET risk_value = val.id FROM amp_category_value val,amp_indicator_risk_ratings risk WHERE ind.risk =risk.amp_ind_risk_ratings_id  and val.category_value=risk.rating_name;
