script.lang:
DROP VIEW IF EXISTS v_mondrian_category_values; CREATE OR REPLACE VIEW v_mondrian_category_values AS SELECT acv.id AS acv_id, acv.category_value AS acv_name, acc.id AS acc_id, acc.category_name AS acc_name, acc.keyname AS acc_key FROM amp_category_value acv LEFT JOIN amp_category_class acc ON acv.amp_category_class_id = acc.id
UNION ALL SELECT 999999999, 'Undefined', 999999999, 'Undefined', 'Undefined'
ORDER BY acv_id;
