script.lang:
		CREATE OR REPLACE VIEW v_budget_extras_fy AS
		SELECT a.amp_activity_id, a.fy
   		FROM amp_activity a
  		ORDER BY a.amp_activity_id;

		INSERT INTO amp_columns (columnId,columnName,aliasName,cellType,extractorView) VALUES 
		((Select (max(columnId) + 1) from amp_columns),'FY','fy','org.dgfoundation.amp.ar.cell.TextCell','v_budget_extras_fy');
