script.lang:
CREATE OR REPLACE VIEW v_donor_funding AS SELECT f.amp_activity_id, f.amp_funding_id, fd.amp_fund_detail_id, d.name AS donor_name, fd.transaction_type, fd.adjustment_type, fd.transaction_date, fd.transaction_amount, fd.pledge_id, c.currency_code, cval.id AS terms_assist_id, cval.category_value AS terms_assist_name, fd.fixed_exchange_rate, b.org_grp_name, ot.org_type AS donor_type_name, cval2.category_value AS financing_instrument_name, cval2.id AS financing_instrument_id, d.amp_org_id AS org_id, d.org_grp_id, ot.amp_org_type_id AS org_type_id, cval3.category_value AS mode_of_payment_name,cval3.id AS mode_of_payment_id, cval4.category_value AS funding_status_name,cval4.id AS funding_status_id
FROM amp_funding f left outer join amp_category_value cval3 on (f.mode_of_payment_category_va=cval3.id) left outer join amp_category_value cval4 on (f.funding_status_category_va=cval4.id), amp_funding_detail fd, amp_category_value cval, amp_currency c, amp_organisation d, amp_org_group b, amp_org_type ot, amp_category_value cval2 WHERE cval2.id = f.financing_instr_category_value AND c.amp_currency_id = fd.amp_currency_id AND f.amp_funding_id = fd.amp_funding_id AND cval.id = f.type_of_assistance_category_va AND d.amp_org_id = f.amp_donor_org_id AND d.org_grp_id = b.amp_org_grp_id AND ot.amp_org_type_id = b.org_type ORDER BY f.amp_activity_id;
CREATE OR REPLACE VIEW v_mode_of_payment AS SELECT a.amp_activity_id, coalesce(val.category_value,'Mode of Payment Unallocated'::varchar(255)) AS mode_of_payment_name, coalesce(val.id,0) AS mode_of_payment_code FROM amp_funding fund left outer join amp_category_value val on (val.id = fund.mode_of_payment_category_va), amp_activity_version a WHERE fund.amp_activity_id = a.amp_activity_id GROUP BY a.amp_activity_id, val.id, val.category_value ORDER BY a.amp_activity_id, val.category_value;
