script.lang:
update amp_activity_version a set activity_creator = updated_by where a.activity_creator is null;
update amp_activity_version a set activity_creator = modified_by where a.activity_creator is null;
update amp_activity_version a set activity_creator = approvedBy where a.activity_creator is null;
update amp_activity_version a set activity_creator = (select amp_team_mem_id from amp_team_member where amp_team_id=a.amp_team_id 
	and amp_member_role_id=(select amp_team_mem_role_id from amp_team_member_roles where role like 'Workspace Manager')) where a.activity_creator is null;
