script.lang:
				CREATE OR REPLACE FUNCTION getparentsubsectorid(sectorid bigint)
  				RETURNS bigint AS
				$BODY$
				 declare
				         sid bigint;
				         depth int;
				 BEGIN                   
				         select getSectorDepth(sectorId) into depth;
				         if (depth = 1) then return sectorId;
				         end if;         
				         if (depth = 2) then 
				                 select parent_sector_id into sid from amp_sector where amp_sector_id=sectorId;
				         return sid;     
				         end if;
				 return 0;
				 END;
				 $BODY$
				 LANGUAGE plpgsql VOLATILE
