script.lang:
      	update amp_report_measures set measureid = 
      		(select min(measureid) from  amp_measures where measurename = 'Planned Disbursements' and type = 'P') 
      		where measureid = (select measureid from amp_measures where measurename = 'Planned Disbursements' and type = 'P' and measureid not in (select min(measureid) from  amp_measures where measurename = 'Planned Disbursements' and type = 'P'));
		update amp_report_measures set measureid = 
			(select min(measureid) from  amp_measures where measurename = 'Planned Commitments' and type = 'P') 
			where measureid = (select measureid from amp_measures where measurename = 'Planned Commitments' and type = 'P' and measureid not in (select min(measureid) from  amp_measures where measurename = 'Planned Commitments' and type = 'P'));
		delete from amp_measures where measurename = 'Planned Disbursements' and type = 'P' and measureid not in (select min(measureid) from  amp_measures where measurename = 'Planned Disbursements' and type = 'P');
		delete from amp_measures where measurename = 'Planned Commitments' and type = 'P' and measureid not in (select min(measureid) from  amp_measures where measurename = 'Planned Commitments' and type = 'P');
