INSERT INTO amp_category_class(id, category_name,is_multiselect,is_ordered, keyName) 
VALUES((select nextval('amp_category_class_seq')), 'Report Categories', false, false, 'report_category');

insert into amp_category_value (id, category_value, amp_category_class_id, index_column) 
select (nextval('amp_category_value_seq')) as id2, 'On budget', id, '0' 
	from amp_category_class where keyname='report_category';

insert into amp_category_value (id, category_value, amp_category_class_id, index_column) 
select (nextval('amp_category_value_seq')) as id2, 'Off budget', id, '1' 
from amp_category_class where keyname='report_category';
