script.lang:
CREATE OR REPLACE VIEW v_m_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)) AS sectorname, getsectorname(getparentsubsectorid(s.amp_sector_id)) AS subsectorname, getsectorname(getparentsubsubsectorid(s.amp_sector_id)) AS subsubsectorname, getparentsectorid(s.amp_sector_id) AS amp_sector_id, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id AS amp_sector_scheme_id, ss.sec_scheme_name FROM amp_sector_scheme ss JOIN amp_classification_config cc ON cc.name::text = 'Primary'::text AND cc.classification_id = ss.amp_sec_scheme_id JOIN amp_sector s ON s.amp_sec_scheme_id = ss.amp_sec_scheme_id JOIN amp_activity_sector sa ON sa.amp_sector_id = s.amp_sector_id AND sa.classification_config_id = cc.id GROUP BY sa.amp_activity_id, getparentsectorid(s.amp_sector_id), s.amp_sec_scheme_id, ss.sec_scheme_name,getparentsubsectorid(s.amp_sector_id),getparentsubsubsectorid
(s.amp_sector_id) ORDER BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id));
CREATE OR REPLACE VIEW v_m_secondary_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)) AS sectorname, getparentsectorid(s.amp_sector_id) AS amp_sector_id, getsectorname(getparentsubsectorid(s.amp_sector_id)) AS subsectorname, getsectorname(getparentsubsubsectorid(s.amp_sector_id)) AS subsubsectorname, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id AS amp_sector_scheme_id, ss.sec_scheme_name FROM amp_activity_sector sa, amp_sector s, amp_sector_scheme ss WHERE s.amp_sector_id = sa.amp_sector_id AND (s.amp_sec_scheme_id IN ( SELECT amp_classification_config.classification_id FROM amp_classification_config WHERE amp_classification_config.name::text = 'Secondary'::text)) AND s.amp_sec_scheme_id = ss.amp_sec_scheme_id GROUP BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)), getparentsectorid(s.amp_sector_id), s.amp_sec_scheme_id, getparentsubsectorid(s.amp_sector_id), getparentsubsubsectorid(s.amp_sector_id), ss.sec_scheme_name ORDER BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id));
