script.lang:
insert into amp_menu_entry (id,parent_id,name,title,url,position) values
(nextval('amp_menu_entry_seq'),(select id from amp_menu_entry where name='Tools'),'IATI Tool','IATI Tool','/import-tool',4);
INSERT INTO amp_menu_entry_view(menu_id, view_type) 
VALUES ((SELECT id FROM amp_menu_entry WHERE name = 'IATI Tool'), 2);
INSERT INTO amp_visibility_rule(SELECT nextval('amp_visibility_rule_seq'), null, 1);
UPDATE amp_menu_entry_view SET rule_id=(SELECT currval('amp_visibility_rule_seq')) WHERE menu_id= (SELECT id FROM amp_menu_entry WHERE name = 'IATI Tool') AND view_type=0;
INSERT INTO amp_modules_visibility (id,name,haslevel,parent)
SELECT (select nextval('amp_modules_visibility_seq')),'IATI Tool',true,
(SELECT id FROM amp_modules_visibility WHERE name = 'Tools')
WHERE 
NOT EXISTS (SELECT id from amp_modules_visibility  WHERE name  = 'IATI Tool');
INSERT INTO amp_visibility_rule_amp_modules_visibility(SELECT currval('amp_visibility_rule_seq'), 
(SELECT id FROM amp_modules_visibility WHERE name = 'IATI Tool' 
AND parent IN (SELECT id FROM amp_modules_visibility WHERE name = 'Tools')));
