				UPDATE amp_report_measures SET measureid = (SELECT measureid FROM amp_measures WHERE measurename = 'Uncommitted Balance') 
						 WHERE measureid = (SELECT measureid FROM amp_measures WHERE measurename = 'Uncommitted Cumulative Balance');

				UPDATE amp_report_measures SET measureid = (SELECT measureid FROM amp_measures WHERE measurename = 'Disbursment Ratio') 
						 WHERE measureid = (SELECT measureid FROM amp_measures WHERE measurename = 'Percentage Of Total Disbursements');

				UPDATE amp_report_measures SET measureid = (SELECT measureid FROM amp_measures WHERE measurename = 'Total Commitments') 
						 WHERE measureid = (SELECT measureid FROM amp_measures WHERE measurename = 'Cumulative Commitment');
