script.lang:
CREATE OR REPLACE VIEW v_sub_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsubsectorid(s.amp_sector_id)) AS sectorname, getparentsubsectorid(s.amp_sector_id) AS amp_sector_id, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id, ss.sec_scheme_name FROM amp_sector_scheme ss JOIN amp_classification_config cc ON cc.name::text = 'Primary'::text AND cc.classification_id = ss.amp_sec_scheme_id JOIN amp_sector s ON s.amp_sec_scheme_id = ss.amp_sec_scheme_id AND getsectordepth(s.amp_sector_id) >= 1 JOIN amp_activity_sector sa ON sa.amp_sector_id = s.amp_sector_id AND sa.classification_config_id = cc.id GROUP BY sa.amp_activity_id, getparentsubsectorid(s.amp_sector_id), s.amp_sec_scheme_id, ss.sec_scheme_name ORDER BY sa.amp_activity_id, getsectorname(getparentsubsectorid(s.amp_sector_id));
CREATE OR REPLACE VIEW v_sub_sub_sectors AS SELECT a.amp_activity_id, s.name, s.amp_sector_id, a.sector_percentage FROM amp_activity_sector a, amp_sector s WHERE a.amp_sector_id = s.amp_sector_id AND getsectordepth(s.amp_sector_id) = 2 AND (s.amp_sec_scheme_id IN ( SELECT c.classification_id FROM amp_classification_config c WHERE c.name::text = 'Primary'::text)) ORDER BY a.amp_activity_id, s.name;
CREATE OR REPLACE VIEW v_secondary_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)) AS sectorname, getparentsectorid(s.amp_sector_id) AS amp_sector_id, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id AS amp_sector_scheme_id FROM amp_activity_sector sa, amp_sector s, amp_sector_scheme ss WHERE s.amp_sector_id = sa.amp_sector_id AND (s.amp_sec_scheme_id IN ( SELECT amp_classification_config.classification_id FROM amp_classification_config WHERE amp_classification_config.name::text = 'Secondary'::text)) AND s.amp_sec_scheme_id = ss.amp_sec_scheme_id GROUP BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)), getparentsectorid(s.amp_sector_id), s.amp_sec_scheme_id ORDER BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id));
CREATE OR REPLACE VIEW v_secondary_sub_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsubsectorid(s.amp_sector_id)) AS sectorname, getparentsubsectorid(s.amp_sector_id) AS amp_sector_id, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id, ss.sec_scheme_name FROM amp_sector_scheme ss JOIN amp_classification_config cc ON cc.name::text = 'Secondary'::text AND cc.classification_id = ss.amp_sec_scheme_id JOIN amp_sector s ON s.amp_sec_scheme_id = ss.amp_sec_scheme_id AND getsectordepth(s.amp_sector_id) >= 1 JOIN amp_activity_sector sa ON sa.amp_sector_id = s.amp_sector_id AND sa.classification_config_id = cc.id GROUP BY sa.amp_activity_id, getparentsubsectorid(s.amp_sector_id), s.amp_sec_scheme_id, ss.sec_scheme_name ORDER BY sa.amp_activity_id, getsectorname(getparentsubsectorid(s.amp_sector_id));
CREATE OR REPLACE VIEW v_secondary_sub_sub_sectors AS SELECT a.amp_activity_id, s.name, s.amp_sector_id, a.sector_percentage FROM amp_activity_sector a, amp_sector s WHERE a.amp_sector_id = s.amp_sector_id AND getsectordepth(s.amp_sector_id) = 2 AND (s.amp_sec_scheme_id IN ( SELECT c.classification_id FROM amp_classification_config c WHERE c.name::text = 'Secondary'::text)) ORDER BY a.amp_activity_id, s.name;
CREATE OR REPLACE VIEW v_tertiary_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)) AS sectorname, getparentsectorid(s.amp_sector_id) AS amp_sector_id, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id AS amp_sector_scheme_id FROM amp_activity_sector sa, amp_sector s, amp_sector_scheme ss WHERE s.amp_sector_id = sa.amp_sector_id AND (s.amp_sec_scheme_id IN ( SELECT amp_classification_config.classification_id FROM amp_classification_config WHERE amp_classification_config.name::text = 'Tertiary'::text)) AND s.amp_sec_scheme_id = ss.amp_sec_scheme_id GROUP BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id)), s.amp_sector_id, s.amp_sec_scheme_id ORDER BY sa.amp_activity_id, getsectorname(getparentsectorid(s.amp_sector_id));
CREATE OR REPLACE VIEW v_tertiary_sub_sectors AS SELECT sa.amp_activity_id, getsectorname(getparentsubsectorid(s.amp_sector_id)) AS sectorname, getparentsubsectorid(s.amp_sector_id) AS amp_sector_id, sum(sa.sector_percentage) AS sector_percentage, s.amp_sec_scheme_id, ss.sec_scheme_name FROM amp_sector_scheme ss JOIN amp_classification_config cc ON cc.name::text = 'Tertiary'::text AND cc.classification_id = ss.amp_sec_scheme_id JOIN amp_sector s ON s.amp_sec_scheme_id = ss.amp_sec_scheme_id AND getsectordepth(s.amp_sector_id) >= 1 JOIN amp_activity_sector sa ON sa.amp_sector_id = s.amp_sector_id AND sa.classification_config_id = cc.id GROUP BY sa.amp_activity_id, getparentsubsectorid(s.amp_sector_id), s.amp_sec_scheme_id, ss.sec_scheme_name ORDER BY sa.amp_activity_id, getsectorname(getparentsubsectorid(s.amp_sector_id));
CREATE OR REPLACE VIEW v_tertiary_sub_sub_sectors AS SELECT a.amp_activity_id, s.name, s.amp_sector_id, a.sector_percentage FROM amp_activity_sector a, amp_sector s WHERE a.amp_sector_id = s.amp_sector_id AND getsectordepth(s.amp_sector_id) = 2 AND (s.amp_sec_scheme_id IN ( SELECT c.classification_id FROM amp_classification_config c WHERE c.name::text = 'Tertiary'::text)) ORDER BY a.amp_activity_id, s.name;
