script.lang:
		drop view IF EXISTS v_budget_structure;
		create or replace view v_budget_structure as (SELECT bs.amp_activity_id,
		bs.budget_structure_name,
 		cv.id,bs.budget_structure_percentage 
		from amp_activity_budget_structure bs,amp_category_value cv where bs.budget_structure_name = cv.category_value order by bs.amp_activity_id,cv.id);
