script.lang:
	DROP VIEW IF EXISTS v_component_funding_organization_name;
   	CREATE OR REPLACE VIEW v_component_funding_organization_name AS 
		SELECT f.activity_id AS amp_activity_id, f.description AS title, f.amp_component_id AS amp_component_id
		FROM amp_component_funding f, amp_components c, amp_activity_components aac, amp_activity activ
		WHERE f.amp_component_id = c.amp_component_id AND f.activity_id = aac.amp_activity_id AND activ.amp_activity_id = aac.amp_activity_id AND f.amp_component_id = aac.amp_component_id
	ORDER BY f.activity_id;
