DROP VIEW IF EXISTS v_ni_gpi_indicator_9b_q1 CASCADE; CREATE VIEW v_ni_gpi_indicator_9b_q1 AS SELECT r.amp_gpi_ni_survey_response_id as response_id, aor.activity as activity_id, aor.organisation as donor_id, aor.role as role, CASE WHEN opt.description = 'Yes' THEN 1 WHEN opt.description = 'No' THEN 2 ELSE NULL END AS response FROM amp_gpi_ni_survey_response r JOIN amp_gpi_ni_survey s ON s.amp_gpi_ni_survey_id = r.amp_gpi_ni_survey_id JOIN amp_org_role aor ON s.amp_org_role_id = aor.amp_org_role_id JOIN amp_gpi_ni_indicator ind ON ind.code = '9b' JOIN amp_gpi_ni_question q ON q.amp_gpi_ni_indicator_id = ind.amp_gpi_ni_indicator_id AND r.amp_gpi_ni_question_id = q.amp_gpi_ni_question_id AND q.code = '1' LEFT JOIN amp_gpi_ni_question_option opt ON opt.amp_gpi_ni_question_option_id = r.question_option_id;
