script.lang:
	DROP VIEW IF EXISTS v_pledges_donor_type;
    CREATE OR REPLACE VIEW v_pledges_donor_type AS 
    	SELECT f.id AS pledge_id, ot.org_type, ot.amp_org_type_id AS org_type_id 
    		FROM amp_funding_pledges f, amp_organisation o, amp_org_group gr, amp_org_type ot 
    		WHERE (((f.amp_org_id = o.amp_org_id) AND (o.org_grp_id = gr.amp_org_grp_id)) AND (ot.amp_org_type_id = gr.org_type)) 
    		ORDER BY f.id, ot.org_type;
