script.lang:
	DROP VIEW IF EXISTS v_regions;
	CREATE OR REPLACE VIEW v_regions AS 
    	SELECT ra.amp_activity_id, regions.region_name AS region, regions.region_id AS region_id, sum(ra.location_percentage) AS location_percentage 
			FROM amp_activity_location ra, amp_location_cache regions
			WHERE (ra.amp_location_id = regions.amp_location_id) AND (regions.region_id IS NOT NULL)
			GROUP BY ra.amp_activity_id, regions.region_id, regions.region_name;
