insert INTO amp_category_class(category_name,is_multiselect,is_ordered, keyName) SELECT 'Line Ministry Rank', b'0', b'0', 'line_ministry_rank' FROM dual where (select count(category_name) from amp_category_class cat where cat.keyName='line_ministry_rank')=0;
insert INTO amp_category_value(category_value, amp_category_class_id, index_column ) SELECT '1',acc.id,0 FROM amp_category_class acc WHERE acc.keyName='line_ministry_rank' and (select count(cat.category_value) from amp_category_value cat where cat.amp_category_class_id=acc.id and cat.index_column=0)=0;
