INSERT INTO `amp_columns_filters` VALUES (1,getReportColumnId('Primary Sector'),'selectedSecondarySectors','amp_sector_id'),(2,getReportColumnId('National Planning Objectives'),'selectedNatPlanObj','amp_program_id'),(3,getReportColumnId('Primary Program'),'selectedPrimaryPrograms','amp_program_id'),(4,getReportColumnId('Secondary Program'),'selectedSecondaryPrograms','amp_program_id'),(5,getReportColumnId('Region'),'regionSelected','region_id'),(6,getReportColumnId('Executing Agency'),'executingAgency','amp_org_id'),(7,getReportColumnId('Implementing Agency'),'implementingAgency','amp_org_id'),(8,getReportColumnId('Beneficiary Agency'),'beneficiaryAgency','amp_org_id'),(9,getReportColumnId('Financing Instrument'),'financingInstruments','amp_modality_id'),(10,getReportColumnId('Type Of Assistance'),'typeOfAssistance','terms_assist_code'),(11,getReportColumnId('Donor Group'),'donorGroups','amp_org_grp_id'),(12,getReportColumnId('Donor Type'),'donorTypes','org_type_id'),(13,getReportColumnId('Donor Agency'),'donorGroups','org_grp_id'),(14,getReportColumnId('Donor Agency'),'donorTypes','org_type_id'),(15,getReportColumnId('Primary Sector'),'selectedSectors','amp_sector_id'),(16,getReportColumnId('Donor Agency'),'donnorgAgency','amp_donor_org_id');