script.lang:
  	CREATE OR REPLACE VIEW `v_component_type` AS
  	select `aac`.`amp_activity_id` AS `amp_activity_id`,
       `ct`.`category_value` AS `component_type`,
       `ct`.`id` AS `component_type_id`
		from ((`amp_components` `c` join `amp_category_value` `ct`) join `amp_activity_components` `aac`)
		where ((`c`.`type` = `ct`.`id`) and (`aac`.`amp_component_id` = `c`.`amp_component_id`));
